#include <iostream>
#include <eigen3/Eigen/Dense> // 下面是基础的线性代数组解法 Eigen::Matrix3f A; A << 1,1,1, 0,2,5 , 2,5,-1; Eigen::Vector3f B; B << 6, -4, 27; std::cout << "Here is the matrix A:\n" << A << std::endl; std::cout << "Here is the vector b:\n" << B << std::endl; Eigen::Vector3f x = A.colPivHouseholderQr().solve(B); std::cout << "The solution is:\n" << x << std::endl; // Here is the matrix A:
// 1 1 1
// 0 2 5
// 2 5 -1
// Here is the vector b:
// 6
// -4
// 27
// The solution is:
// 5
// 3
// -2

